'BEGIN',
'    v_department := :P7_DEPARTMENT_VALUE;',
'    ',
'    IF v_department IS NOT NULL AND v_department LIKE ''%Marketing%Activity%'' THEN',
'        RETURN TRUE;',
'    ELSE',
'        RETURN FALSE; ',
'    END IF;',
'END;'))
,p_plug_display_when_cond2=>'PLSQL'
);
