,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Synchronize Data'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/* Formatted on 10/20/2025 3:47:25 AM (QP5 v5.362) */',
'DECLARE',
'    v_dept_name    APX_BF_BUDGET_MONITOR_HDR.DEPT_NAME%TYPE;',
'    v_header_id    NUMBER := :P7_HEADER_ID;',
'    v_year_value   VARCHAR2 (50);',
'    v_count        NUMBER;',
'BEGIN',
'    -------------------------------------------------------------------------',
'    -- STEP 0: Get department name',
'    -------------------------------------------------------------------------',
'    BEGIN',
'        SELECT DEPT_NAME, YEAR',
'          INTO v_dept_name, v_year_value',
'          FROM APX_BF_BUDGET_MONITOR_HDR',
'         WHERE ID = v_header_id;',
'',
'        apex_debug.MESSAGE (',
'            ''Step 0: Department name fetched successfully: %s | Year: %s'',',
'            v_dept_name,',
'            v_year_value);',
'    EXCEPTION',
'        WHEN NO_DATA_FOUND',
'        THEN',
'            apex_debug.MESSAGE (',
'                ''Step 0: No department found for HEADER_ID = %s'',',
'                v_header_id);',
'            RAISE;',
'        WHEN OTHERS',
'        THEN',
'            apex_debug.MESSAGE (''Step 0: Error fetching department name: %s'',',
'                                SQLERRM);',
'            RAISE;',
'    END;',
'',
'    -------------------------------------------------------------------------',
'    -- STEP 1: Mark existing records in main table as ''Sync''',
'    -------------------------------------------------------------------------',
'    BEGIN',
'        UPDATE APX_BF_COMM_ACT_AMT',
'           SET SYNC_STATUS = ''Sync''',
'         WHERE     DEPT_NAME_SYNC = v_dept_name',
'               AND SUBSTR (PERIOD, -2) = SUBSTR (v_year_value, -2);',
'',
'        apex_debug.MESSAGE (''Step 1: Updated main table to Sync: %s row(s)'',',
'                            SQL%ROWCOUNT);',
'    EXCEPTION',
'        WHEN OTHERS',
'        THEN',
'            apex_debug.MESSAGE (''Step 1: Error updating Sync status: %s'',',
'                                SQLERRM);',
'            RAISE;',
'    END;',
'',
'    -------------------------------------------------------------------------',
'    -- STEP 2: Clean up old staging data',
'    -------------------------------------------------------------------------',
'    BEGIN',
'        DELETE APX_BF_COMM_ACT_AMT_STG',
'         WHERE     DEPT_NAME_SYNC = v_dept_name',
'               AND SUBSTR (PERIOD, -2) = SUBSTR (v_year_value, -2);',
'',
'        apex_debug.MESSAGE (''Step 2: Deleted old staging data: %s row(s)'',',
'                            SQL%ROWCOUNT);',
'    EXCEPTION',
'        WHEN OTHERS',
'        THEN',
'            apex_debug.MESSAGE (',
'                ''Step 2: Error deleting old staging data: %s'',',
'                SQLERRM);',
'            RAISE;',
'    END;',
'',
'    -------------------------------------------------------------------------',
'    -- STEP 3: Insert new data into staging table',
'    -------------------------------------------------------------------------',
'    BEGIN',
'        INSERT INTO APX_BF_COMM_ACT_AMT_STG (COMPANY,',
'                                             SOURCE,',
'                                             SOURCE_STATUS,',
'                                             ACTIVITY,',
'                                             COMMITMENT,',
'                                             ACTUAL_AMOUNT,',
'                                             PERIOD,',
'                                             PR_QUANTITY,',
'                                             PO_QUANTITY,',
'                                             RECEIPT_QUANTITY,',
'                                             INVOICED_QUANTITY,',
'                                             PR_COMMITMENT,',
'                                             PO_COMMITMENT,',
'                                             RCV_ACTUAL,',
'                                             INVOICE_ACTUAL,',
'                                             GL_MANUAL_ACTUAL,',
'                                             AR_INVOICE_ACTUAL,',
'                                             AR_MISC_ACTUAL,',
'                                             INVENTORY_ACTUAL,',
'                                             PR_NUMBER,',
'                                             PO_NUMBER,',
'                                             RCV_NUMBER,',
'                                             INVOICE_NUMBER,',
'                                             INVENTORY_NUMBER,',
'                                             JOURNAL_NAME,',
'                                             JOURNAL_BATCH,',
'                                             JOURNAL_LINE_DESC,',
'                                             AR_INVOICE_NUM,',
'                                             AR_MISC_RECEIPT,',
'                                             REQ_HEADER_ID,',
'                                             REQ_LINE_ID,',
'                                             REQ_DISTRIBUTION_ID,',
'                                             PO_HEADER_ID,',
'                                             PO_LINE_ID,',
'                                             PO_DISTRIBUTION_ID,',
'                                             PO_STATUS,',
'                                             RCV_ID,',
'                                             TRANSACTION_ID,',
'                                             INVOICE_ID,',
'                                             INVOICE_LINE_NUM,',
'                                             INVOICE_DISTRIBUTION_ID,',
'                                             JE_HEADER_ID,',
'                                             INVENTORY_TRX_ID,',
'                                             INV_ACTUAL_COST,',
'                                             INV_QUANTITY,',
'                                             INV_TRX_NUMBER,',
'                                             INV_DESC,',
'                                             INV_TRX_TYPE,',
'                                             INV_SOURCE_NAME,',
'                                             AR_TRX_NUMBER,',
'                                             AR_UNIT_PRICE,',
'                                             AR_UNIT_QUANTITY,',
'                                             AR_TOTAL_AMOUNT,',
'                                             AR_MISC_TRX_ID,',
'                                             AR_MISC_STATUS,',
'                                             AR_MISC_COMMENT,',
'                                             FA_DEPRN_AMOUNT,',
'                                             FA_ASSET_ID,',
'                                             FA_DISTRIBUTION_ID,',
'                                             FA_INVOICE_ID,',
'                                             FA_ITEM_DESC,',
'                                             OPM_ADJ_ID,',
'                                             OPM_ADJUST_QTY,',
'                                             OPM_ADJUST_COST,',
'                                             OPM_COST_TYPE_ID,',
'                                             OPM_DESCRIPTION,',
'                                             OPM_ITEM,',
'                                             OPM_PERIOD,',
'                                             LAST_SYNCHRONIZED,',
'                                             DEPT_NAME_SYNC,',
'                                             SYNC_STATUS)',
'            SELECT base.company,',
'                   base.source,',
'                   base.source_status,',
'                   base.activity,',
'                   base.commitment,',
'                   base.actual_amount,',
'                   base.period,',
'                   base.pr_quantity,',
'                   base.po_quantity,',
'                   base.receipt_quantity,',
'                   base.invoiced_quantity,',
'                   base.pr_commitment,',
'                   base.po_commitment,',
'                   base.rcv_actual,',
'                   base.invoice_actual,',
'                   base.gl_manual_actual,',
'                   base.ar_invoice_actual,',
'                   base.ar_misc_actual,',
'                   base.inventory_actual,',
'                   base.pr_number,',
'                   base.po_number,',
'                   base.rcv_number,',
'                   base.invoice_number,',
