'                   base.ar_misc_receipt,',
'                   base.req_header_id,',
'                   base.req_line_id,',
'                   base.req_distribution_id,',
'                   base.po_header_id,',
'                   base.po_line_id,',
'                   base.po_distribution_id,',
'                   base.po_status,',
'                   base.rcv_id,',
'                   base.transaction_id,',
'                   base.invoice_id,',
'                   base.invoice_line_num,',
'                   base.invoice_distribution_id,',
'                   base.je_header_id,',
'                   base.inventory_trx_id,',
