'    FUNCTION get_user_role (',
'        p_app_id IN NUMBER,',
'        p_username IN VARCHAR2',
'    ) RETURN VARCHAR2 IS',
'        v_role VARCHAR2(100);',
'    BEGIN',
'        -- Check FPNA first',
'        IF APEX_ACL.HAS_USER_ROLE(p_app_id, p_username, ''Administrator'') THEN',
'            RETURN ''Administrator'';',
'        ELSIF APEX_ACL.HAS_USER_ROLE(p_app_id, p_username, ''FPNA'') THEN',
'            RETURN ''FPNA'';',
'        ELSIF APEX_ACL.HAS_USER_ROLE(p_app_id, p_username, ''BOD'') THEN',
'            RETURN ''BOD'';',
'        ELSIF APEX_ACL.HAS_USER_ROLE(p_app_id, p_username, ''BUDGET_FORECAST'') THEN',
'            RETURN ''BUDGET FORECAST'';',
'        ELSIF APEX_ACL.HAS_USER_ROLE(p_app_id, p_username, ''DEPARTMENT'') THEN',
'            RETURN ''DEPARTMENT'';',
'        ELSIF APEX_ACL.HAS_USER_ROLE(p_app_id, p_username, ''Contributor'') THEN',
'            RETURN ''Contributor'';',
'        ELSIF APEX_ACL.HAS_USER_ROLE(p_app_id, p_username, ''Reader'') THEN',
'            RETURN ''Reader'';',
'        ELSE',
'            RETURN NULL;',
'        END IF;',
'    EXCEPTION',
'        WHEN OTHERS THEN',
'            RETURN NULL;',
'    END;',
