'',
'',
'----------------------------------------------------------------',
'-- MAIN QUERY',
'SELECT base.rcv_number AS transaction, ',
'       ''RECEIPT NUMBER'' AS type, ',
'       base.source as source,',
'       SUM(base.actual_amount) AS actual_amount',
