,p_location=>'WEB_SOURCE'
,p_web_src_module_id=>wwv_flow_imp.id(1377713632695458015)
,p_use_local_sync_table=>true
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT *',
'FROM (',
'SELECT ',
'       NVL(TO_CHAR(REST.ID), ''REST_'' || ROWNUM) AS LINK_KEY, -- Use ID if exists, otherwise generate surrogate for linking',
'       REST.ID,',
'       REST.COA,',
'       REST.PRICE,',
'       REST.PERIOD,',
'       REST.ACTIVITY,',
'       REST.LOCATION,',
'       REST.HEADER_ID,',
'       NVL(CA.TOTAL_COMMITMENT, 0) AS COMMITMENT,',
'       REST.CREATED_BY,',
'       REST.UPDATED_BY,',
'       REST.VERSION_NO,',
'       REST.COST_CENTER,',
'       REST.LINE_NUMBER,',
'       REST.LINE_SOURCE,',
'       REST.COMPANY_NAME,',
'       REST.CREATED_DATE,',
'       REST.FINAL_AMOUNT,',
'       REST.REJECT_NOTES,',
'       REST.UPDATED_DATE,',
'       NVL(CA.TOTAL_ACTUAL_AMOUNT, 0) + NVL (',
'           CASE WHEN REV.CATEGORY = ''Revenue'' THEN REV.NET_SALES',
'           WHEN REV.CATEGORY = ''COGS'' THEN REV.HPP',
'           END, 0) AS ACTUAL_AMOUNT,',
'       NVL((REST.FINAL_AMOUNT - NVL(CA.TOTAL_COMMITMENT, 0) - NVL(CA.TOTAL_ACTUAL_AMOUNT, 0) - NVL(',
'           CASE WHEN REV.CATEGORY = ''Revenue'' THEN REV.NET_SALES',
'           WHEN REV.CATEGORY = ''COGS'' THEN REV.HPP',
'           END, 0)), 0) AS FUND_AVAILABLE,',
'       REST.BUDGET_CATEGORY,',
'       NVL((',
'           SELECT MAX(orig.ORIGINAL_AMOUNT)',
'             FROM APX_BF_BUDGET_MONITOR_LNS_V orig',
'            WHERE orig.COST_CENTER     = REST.COST_CENTER',
'              AND orig.BUDGET_CATEGORY = REST.BUDGET_CATEGORY',
'              AND orig.ACTIVITY        = REST.ACTIVITY',
'              AND orig.COA             = REST.COA',
'              AND orig.PERIOD          = REST.PERIOD',
'              AND orig.VERSION_NO      = 0',
'              AND ROWNUM = 1',
'       ), REST.ORIGINAL_AMOUNT) AS ORIGINAL_AMOUNT,',
'       CA.TRX_COMMITMENT_NUMBER,',
'       CA.TRX_ACTUAL_NUMBER,',
'       ca.activity as TRX_ACTIVITY,',
'       REV.CATEGORY AS REV_COA,',
'       REV.PRODUCT AS REV_PRODUCT,',
'       TRIM(CA.LOCATION) as TRX_LOCATION ',
'  FROM #APEX$SOURCE_DATA# REST',
'  JOIN APX_BF_BUDGET_MONITOR_HDR H',
'         on H.ID = :P7_HEADER_ID',
'  LEFT JOIN APX_BF_COMM_ACT_SUM_V CA',
'  ON REST.ACTIVITY = TRIM(REGEXP_SUBSTR(CA.ACTIVITY, ''[^|]+'', 1, 1))',
'     AND TRIM(REGEXP_SUBSTR(CA.ACTIVITY, ''[^|]+'', 1, 2)) = H.DEPT_NAME',
'     AND SUBSTR(CA.PERIOD, 1, 3) = SUBSTR(REST.PERIOD, 1, 3)',
'     AND SUBSTR(CA.PERIOD, -2) = SUBSTR(:P7_YEAR_VALUE, -2)',
'     AND CA.COMPANY = :P7_COMPANY_VALUE',
'     AND TRIM(CA.LOCATION) = TRIM(REGEXP_SUBSTR(REST.LOCATION, ''[^-]+'', 1, 1))',
'  LEFT JOIN APX_BF_REVENUE_COGS_AMT_V REV',
'    ON  REGEXP_SUBSTR(REST.PRODUCT, ''^\d+'') = REV.PRODUCT',
'        AND REST.BUDGET_CATEGORY = REV.CATEGORY',
'        AND TRIM(REV.COMPANY) = :P7_COMPANY_VALUE',
'        AND TRIM(REV.BULAN) = TRIM(REST.PERIOD)',
' WHERE REST.HEADER_ID = :P7_HEADER_ID',
'',
' UNION ALL',
'',
' SELECT ',
'       ''UNB_'' || ROWNUM AS LINK_KEY, -- Generate a unique link key for unbudgeted rows',
'       NULL ID,',
'       NULL COA,',
'       NULL PRICE,',
'       CA.PERIOD AS PERIOD,',
'       TRIM(REGEXP_SUBSTR(CA.ACTIVITY, ''[^|]+'', 1, 1)) AS ACTIVITY,',
'       xbl.LOCATION AS LOCATION,',
'       NULL HEADER_ID,',
'       NVL(CA.TOTAL_COMMITMENT, 0) AS COMMITMENT,',
'       NULL CREATED_BY,',
'       NULL UPDATED_BY,',
'       NULL VERSION_NO,',
'       NULL COST_CENTER,',
'       CASE ',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''JAN'' THEN 1',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''FEB'' THEN 2',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''MAR'' THEN 3',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''APR'' THEN 4',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''MAY'' THEN 5',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''JUN'' THEN 6',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''JUL'' THEN 7',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''AUG'' THEN 8',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''SEP'' THEN 9',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''OCT'' THEN 10',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''NOV'' THEN 11',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''DEC'' THEN 12',
'           ELSE 0',
'       END LINE_NUMBER,',
'       NULL LINE_SOURCE,',
'       NULL COMPANY_NAME,',
'       NULL CREATED_DATE,',
'       0 FINAL_AMOUNT,',
'       NULL REJECT_NOTES,',
'       NULL UPDATED_DATE,',
'       NVL(CA.TOTAL_ACTUAL_AMOUNT, 0) AS ACTUAL_AMOUNT,',
'       NVL((0 - NVL(CA.TOTAL_COMMITMENT, 0) - NVL(CA.TOTAL_ACTUAL_AMOUNT, 0)), 0) AS FUND_AVAILABLE,',
'       NULL BUDGET_CATEGORY,',
'       0 ORIGINAL_AMOUNT,',
'       CA.TRX_COMMITMENT_NUMBER,',
'       CA.TRX_ACTUAL_NUMBER,',
'       ca.activity as TRX_ACTIVITY,',
'       NULL AS REV_COA,',
'       NULL AS REV_PRODUCT,',
'       TRIM(CA.LOCATION) as TRX_LOCATION ',
'  FROM APX_BF_COMM_ACT_SUM_V CA ',
'  LEFT JOIN APPS.XTD_BF_HR_LOCATIONS_V xbl',
'       on xbl.company = :P7_COMPANY_VALUE',
'       and xbl.account = ca.location',
'  WHERE 1=1',
'   AND TRIM(REGEXP_SUBSTR(CA.ACTIVITY, ''[^|]+'', 1, 1)) = ''Unbudgeted''',
'   AND TRIM(REGEXP_SUBSTR(CA.ACTIVITY, ''[^|]+'', 1, 2)) = (',
'        SELECT ',
'        DEPT_NAME',
'    FROM ',
'        APX_BF_BUDGET_MONITOR_HDR',
'    WHERE',
'        ID = :P7_HEADER_ID',
'   )',
'   AND SUBSTR(CA.PERIOD, -2) = SUBSTR(:P7_YEAR_VALUE, -2)',
'   AND CA.COMPANY = :P7_COMPANY_VALUE',
'',
' UNION ALL',
'',
' SELECT ',
'        -- Generate a unique link key for unbudgeted rows',
'       ''INV_'' || ROWNUM AS LINK_KEY,',
'       NULL ID,',
'       NULL COA,',
'       NULL PRICE,',
'       CA.PERIOD AS PERIOD,',
'       TRIM(REGEXP_SUBSTR(CA.ACTIVITY, ''[^|]+'', 1, 1)) AS ACTIVITY,',
'       NULL LOCATION,',
'       NULL HEADER_ID,',
'       NVL(CA.TOTAL_COMMITMENT, 0) AS COMMITMENT,',
'       NULL CREATED_BY,',
'       NULL UPDATED_BY,',
'       NULL VERSION_NO,',
'       NULL COST_CENTER,',
'       CASE ',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''JAN'' THEN 1',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''FEB'' THEN 2',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''MAR'' THEN 3',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''APR'' THEN 4',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''MAY'' THEN 5',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''JUN'' THEN 6',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''JUL'' THEN 7',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''AUG'' THEN 8',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''SEP'' THEN 9',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''OCT'' THEN 10',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''NOV'' THEN 11',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''DEC'' THEN 12',
'           ELSE 0',
'       END LINE_NUMBER,',
'       NULL LINE_SOURCE,',
'       NULL COMPANY_NAME,',
'       NULL CREATED_DATE,',
'       0 FINAL_AMOUNT,',
'       NULL REJECT_NOTES,',
'       NULL UPDATED_DATE,',
'       NVL(CA.TOTAL_ACTUAL_AMOUNT, 0) AS ACTUAL_AMOUNT,',
'       NVL((0 - NVL(CA.TOTAL_COMMITMENT, 0) - NVL(CA.TOTAL_ACTUAL_AMOUNT, 0)), 0) AS FUND_AVAILABLE,',
'       NULL BUDGET_CATEGORY,',
'       0 ORIGINAL_AMOUNT,',
'       CA.TRX_COMMITMENT_NUMBER,',
'       CA.TRX_ACTUAL_NUMBER,',
'       ca.activity as TRX_ACTIVITY,',
'       NULL AS REV_COA,',
'       NULL AS REV_PRODUCT,',
'       TRIM(CA.LOCATION) as TRX_LOCATION ',
'  FROM APX_BF_COMM_ACT_SUM_V CA ',
'  WHERE 1=1',
'   AND TRIM(REGEXP_SUBSTR(CA.ACTIVITY, ''[^|]+'', 1, 1)) = ''Inventory''',
'   AND TRIM(REGEXP_SUBSTR(CA.ACTIVITY, ''[^|]+'', 1, 2)) = (',
'        SELECT ',
'        DEPT_NAME',
'    FROM ',
'        APX_BF_BUDGET_MONITOR_HDR',
'    WHERE',
'        ID = :P7_HEADER_ID',
'   )',
'   AND SUBSTR(CA.PERIOD, -2) = SUBSTR(:P7_YEAR_VALUE, -2)',
'   AND CA.COMPANY = :P7_COMPANY_VALUE',
'',
' UNION ALL',
'',
' SELECT ',
'        -- Generate a unique link key for unbudgeted rows',
'       ''ADM_'' || ROWNUM AS LINK_KEY,',
'       NULL ID,',
'       NULL COA,',
'       NULL PRICE,',
'       CA.PERIOD AS PERIOD,',
'       TRIM(REGEXP_SUBSTR(CA.ACTIVITY, ''[^|]+'', 1, 1)) AS ACTIVITY,',
'       NULL LOCATION,',
'       NULL HEADER_ID,',
'       NVL(CA.TOTAL_COMMITMENT, 0) AS COMMITMENT,',
'       NULL CREATED_BY,',
'       NULL UPDATED_BY,',
'       NULL VERSION_NO,',
'       NULL COST_CENTER,',
'       CASE ',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''JAN'' THEN 1',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''FEB'' THEN 2',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''MAR'' THEN 3',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''APR'' THEN 4',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''MAY'' THEN 5',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''JUN'' THEN 6',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''JUL'' THEN 7',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''AUG'' THEN 8',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''SEP'' THEN 9',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''OCT'' THEN 10',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''NOV'' THEN 11',
'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''DEC'' THEN 12',
'           ELSE 0',
'       END LINE_NUMBER,',
'       NULL LINE_SOURCE,',
'       NULL COMPANY_NAME,',
'       NULL CREATED_DATE,',
'       0 FINAL_AMOUNT,',
'       NULL REJECT_NOTES,',
'       NULL UPDATED_DATE,',
'       NVL(CA.TOTAL_ACTUAL_AMOUNT, 0) AS ACTUAL_AMOUNT,',
'       NVL((0 - NVL(CA.TOTAL_COMMITMENT, 0) - NVL(CA.TOTAL_ACTUAL_AMOUNT, 0)), 0) AS FUND_AVAILABLE,',
'       NULL BUDGET_CATEGORY,',
'       0 ORIGINAL_AMOUNT,',
'       CA.TRX_COMMITMENT_NUMBER,',
'       CA.TRX_ACTUAL_NUMBER,',
'       ca.activity as TRX_ACTIVITY,',
'       NULL AS REV_COA,',
'       NULL AS REV_PRODUCT,',
'       TRIM(CA.LOCATION) as TRX_LOCATION',
'  FROM APX_BF_COMM_ACT_SUM_V CA ',
'  WHERE 1=1',
'   AND TRIM(REGEXP_SUBSTR(CA.ACTIVITY, ''[^|]+'', 1, 1)) = ''FA Administratif''',
'   AND TRIM(REGEXP_SUBSTR(CA.ACTIVITY, ''[^|]+'', 1, 2)) = (',
'        SELECT ',
'        DEPT_NAME',
'    FROM ',
'        APX_BF_BUDGET_MONITOR_HDR',
'    WHERE',
'        ID = :P7_HEADER_ID',
'   )',
'   AND SUBSTR(CA.PERIOD, -2) = SUBSTR(:P7_YEAR_VALUE, -2)',
'   AND CA.COMPANY = :P7_COMPANY_VALUE',
' ORDER BY LINE_NUMBER ASC',
')',
' ORDER BY ',
'    ACTIVITY,',
'    TO_NUMBER(',
'        CASE ',
'            WHEN SUBSTR(PERIOD, 1, 3) = ''JAN'' THEN 1',
'            WHEN SUBSTR(PERIOD, 1, 3) = ''FEB'' THEN 2',
'            WHEN SUBSTR(PERIOD, 1, 3) = ''MAR'' THEN 3',
'            WHEN SUBSTR(PERIOD, 1, 3) = ''APR'' THEN 4',
