'create or replace PROCEDURE create_apex_user(',
'    p_username IN VARCHAR2,',
'    p_email IN VARCHAR2,',
'    p_password IN VARCHAR2,',
'    p_user_role IN VARCHAR2',
') IS',
'    l_workspace_id NUMBER;',
