begin
wwv_flow_imp_shared.append_to_install_script(
 p_id=>wwv_flow_imp.id(1512700005105943478)
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'ST_CENTER", "LINE_SOURCE", "ACTIVITY_CODE", "BATCH_NO") AS ',
'  WITH header_versions AS (',
'    SELECT ',
'        h.ID,',
'        h.VERSION_NO,',
'        h.BATCH_NO,',
'        h.STATUS,',
'        RANK() OVER (PARTITION BY h.ID ORDER BY h.VERSION_NO) AS version_rank,',
'        FIRST_VALUE(h.VERSION_NO) OVER (',
'            PARTITION BY h.ID ',
'            ORDER BY CASE WHEN h.STATUS = ''APPROVED'' THEN 0 ELSE 1 END, ',
'                     h.VERSION_NO DESC',
'            ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING',
'        ) AS latest_approved_version',
'    FROM ',
'        "APX_BF_BUDGET_ENTRY_HDR" h',
')',
'SELECT ',
'    a.ID,',
'    a.HEADER_ID,',
'    ah.VERSION_NO,',
'    a.BUDGET_CATEGORY,',
'    a.ACTIVITY,',
'    a.COA,',
'    a.COA_DESC,',
'    a.LOCATION,',
'    a.QUANTITY,',
'    a.PRICE,',
'    orig_a.TOTAL AS ORIGINAL_AMOUNT,',
'    NULL AS COMMITMENT,',
'    a.TOTAL AS FINAL_AMOUNT,',
'    NULL AS ACTUAL_USAGE,',
'    NULL AS FUND_AVAILABLE,',
'    a.PERIOD,',
'    a.REJECT_NOTES,',
'    a.CREATED_DATE,',
'    a.CREATED_BY,',
'    a.UPDATED_DATE,',
'    a.UPDATED_BY,',
'    a.LINE_DESCRIPTION,',
'    a.COST_CENTER,',
'    a.LINE_SOURCE,',
'    a.ACTIVITY_CODE,',
'    ah.BATCH_NO',
'FROM ',
'    "APX_BF_BUDGET_ENTRY_LNS" a',
'JOIN ',
'    header_versions ah ',
'    ON a.HEADER_ID = ah.ID ',
'    AND ah.VERSION_NO = ah.latest_approved_version',
'JOIN ',
'    header_versions oh ',
'    ON a.HEADER_ID = oh.ID ',
'    AND oh.version_rank = 1',
'JOIN ',
'    "APX_BF_BUDGET_ENTRY_LNS" orig_a ',
'    ON orig_a.ID = a.ID ',
'    AND orig_a.HEADER_ID = oh.ID;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "APX_BF_COMM_ACT_SUM_V" ("COMPANY", "ACTIVITY", "ACTIVITY_MASTER", "ACTIVITY_CODE", "LOCATION", "PERIOD", "TOTAL_COMMITMENT", "TOTAL_ACTUAL_AMOUNT", "VARIANCE", "ACTUAL_PERCENTAGE", "TRX_COMMITMENT_NUMBER", '
||'"TRX_ACTUAL_NUMBER") AS ',
'  WITH AGGREGATED_DATA AS (',
'    SELECT ',
'        a.COMPANY,',
'        a.PERIOD,',
'        a.ACTIVITY,',
'        CASE ',
'            WHEN INSTR(a.ACTIVITY, ''|'') > 0 THEN ',
'                TRIM(SUBSTR(a.ACTIVITY, 1, INSTR(a.ACTIVITY, ''|'') - 1))',
'            ELSE ',
'                a.ACTIVITY',
'        END AS ACTIVITY_BASE_NAME,',
'        a.LOCATION,',
'        SUM(a.COMMITMENT) AS TOTAL_COMMITMENT,',
'        SUM(a.ACTUAL_AMOUNT) AS TOTAL_ACTUAL_AMOUNT,',
'        SUM(a.COMMITMENT - a.ACTUAL_AMOUNT) AS VARIANCE,',
'        LISTAGG(DISTINCT a.PR_NUMBER, ''|'') WITHIN GROUP (ORDER BY a.PR_NUMBER) AS PR_NUMBERS,',
'        LISTAGG(DISTINCT a.PO_NUMBER, ''|'') WITHIN GROUP (ORDER BY a.PO_NUMBER) AS PO_NUMBERS,',
'        LISTAGG(DISTINCT a.RCV_NUMBER, ''|'') WITHIN GROUP (ORDER BY a.RCV_NUMBER) AS RCV_NUMBERS,',
'        LISTAGG(DISTINCT a.INVOICE_NUMBER, ''|'') WITHIN GROUP (ORDER BY a.INVOICE_NUMBER) AS INVOICE_NUMBERS,',
'        LISTAGG(DISTINCT a.JE_HEADER_ID, ''|'') WITHIN GROUP (ORDER BY a.JE_HEADER_ID) AS JE_HEADER_IDS,',
'        LISTAGG(DISTINCT a.INV_TRX_NUMBER, ''|'') WITHIN GROUP (ORDER BY a.INV_TRX_NUMBER) AS INV_TRX_NUMBERS,',
'        LISTAGG(DISTINCT a.AR_TRX_NUMBER, ''|'') WITHIN GROUP (ORDER BY a.AR_TRX_NUMBER) AS AR_TRX_NUMBERS,',
'        LISTAGG(DISTINCT a.AR_MISC_TRX_ID, ''|'') WITHIN GROUP (ORDER BY a.AR_MISC_TRX_ID) AS AR_MISC_TRX_IDS,',
'        LISTAGG(DISTINCT a.FA_ASSET_ID, ''|'') WITHIN GROUP (ORDER BY a.FA_ASSET_ID) AS FA_ASSET_IDS,',
'        LISTAGG(DISTINCT a.OPM_ADJ_ID, ''|'') WITHIN GROUP (ORDER BY a.OPM_ADJ_ID) AS OPM_ADJ_IDS',
'    FROM APX_BF_COMM_ACT_AMT a',
'    GROUP BY ',
'        a.company,',
'        a.PERIOD,',
'        a.ACTIVITY,',
'        CASE ',
'            WHEN INSTR(a.ACTIVITY, ''|'') > 0 THEN ',
'                TRIM(SUBSTR(a.ACTIVITY, 1, INSTR(a.ACTIVITY, ''|'') - 1))',
'            ELSE ',
'                a.ACTIVITY',
'        END,',
'        a.LOCATION',
')',
'SELECT ',
'    AD.COMPANY,',
'    AD.ACTIVITY,',
'    AM.ACTIVITY_NAME AS ACTIVITY_MASTER,',
'    MAX(AM.ACTIVITY_CODE) AS ACTIVITY_CODE,',
'    AD.LOCATION,',
'    AD.PERIOD,',
'    AD.TOTAL_COMMITMENT,',
'    AD.TOTAL_ACTUAL_AMOUNT,',
'    AD.VARIANCE,',
'    CASE ',
'        WHEN AD.TOTAL_COMMITMENT = 0 THEN 0 ',
'        ELSE ROUND((AD.TOTAL_ACTUAL_AMOUNT / AD.TOTAL_COMMITMENT) * 100, 2) ',
'    END AS ACTUAL_PERCENTAGE,',
'    CASE ',
'        WHEN AD.TOTAL_COMMITMENT is not null THEN',
'               CASE ',
'                    WHEN AD.PR_NUMBERS IS NOT NULL',
'                    THEN ''~PR_NUMBER~ '' || AD.PR_NUMBERS',
'                    ELSE ''''',
'               END',
'               || CASE ',
'                    WHEN AD.PO_NUMBERS IS NOT NULL',
'                    THEN ''~PO_NUMBER~ '' || AD.PO_NUMBERS',
'                    ELSE ''''',
'               END',
'        ELSE NULL',
'    END AS TRX_COMMITMENT_NUMBER,',
'    CASE',
'        WHEN AD.TOTAL_ACTUAL_AMOUNT is not null THEN',
'               CASE ',
'                    WHEN AD.RCV_NUMBERS IS NOT NULL',
'                    THEN ''~RCV_NUMBER~ '' || AD.RCV_NUMBERS',
'                    ELSE ''''',
'               END',
'               || CASE ',
'                    WHEN AD.INVOICE_NUMBERS IS NOT NULL',
'                    THEN ''~INVOICE_NUMBER~ '' || AD.INVOICE_NUMBERS',
'                    ELSE ''''',
'               END',
'               || CASE ',
'                    WHEN AD.JE_HEADER_IDS IS NOT NULL',
'                    THEN ''~JE_HEADER_ID~ '' || AD.JE_HEADER_IDS',
'                    ELSE ''''',
'               END',
'               || CASE ',
'                    WHEN AD.INV_TRX_NUMBERS IS NOT NULL',
'                    THEN ''~INV_TRX_NUMBER~ '' || AD.INV_TRX_NUMBERS',
'                    ELSE ''''',
'               END',
'               || CASE ',
'                    WHEN AD.AR_TRX_NUMBERS IS NOT NULL',
'                    THEN ''~AR_TRX_NUMBER~ '' || AD.AR_TRX_NUMBERS',
'                    ELSE ''''',
'               END',
'               || CASE ',
'                    WHEN AD.AR_MISC_TRX_IDS IS NOT NULL',
'                    THEN ''~AR_MISC_RECEIPT~ '' || AD.AR_MISC_TRX_IDS',
'                    ELSE ''''',
'               END',
'               || CASE ',
'                    WHEN AD.FA_ASSET_IDS IS NOT NULL',
'                    THEN ''~FA_ASSET_ID~ '' || AD.FA_ASSET_IDS',
'                    ELSE ''''',
'               END                                                         ',
'               || CASE ',
'                    WHEN AD.OPM_ADJ_IDS IS NOT NULL',
'                    THEN ''~OPM_ADJ_ID~ '' || AD.OPM_ADJ_IDS',
'                    ELSE ''''',
'               END                                                         ',
'        ELSE NULL',
'   END AS TRX_ACTUAL_NUMBER',
'FROM AGGREGATED_DATA AD',
'LEFT JOIN APX_BF_ACTIVITY_MASTER_V AM ',
'    ON AD.ACTIVITY_BASE_NAME = AM.ACTIVITY_NAME',
'GROUP BY ',
'    AD.COMPANY,',
'    AD.PERIOD,',
'    AD.ACTIVITY,',
'    AM.ACTIVITY_NAME,',
'    AD.LOCATION,',
'    AD.TOTAL_COMMITMENT,',
'    AD.TOTAL_ACTUAL_AMOUNT,',
'    AD.VARIANCE,',
'    AD.PR_NUMBERS,',
'    AD.PO_NUMBERS,',
'    AD.RCV_NUMBERS,',
'    AD.INVOICE_NUMBERS,',
'    AD.JE_HEADER_IDS,',
'    AD.INV_TRX_NUMBERS,',
'    AD.AR_TRX_NUMBERS,',
'    AD.AR_MISC_TRX_IDS,',
'    AD.FA_ASSET_IDS,',
'    AD.OPM_ADJ_IDS',
'ORDER BY ',
'    AD.PERIOD,',
'    AD.ACTIVITY,',
'    AM.ACTIVITY_NAME;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "APX_BF_DELTA_COMM_ACT_AMT_V" ("SOURCE_COMPANY", "PERIOD", "ACTIVITY", "ACTIVITY_NAME", "TOTAL_COMMITMENT", "TOTAL_ACTUAL_AMOUNT", "TRX_COMMITMENT_NUMBER", "TRX_ACTUAL_NUMBER", "TRANSACTION_ID") AS ',
'  SELECT CAA.COMPANY                AS SOURCE_COMPANY,',
'             CAA.PERIOD,',
'             CAA.ACTIVITY,',
'             ABA.ACTIVITY_NAME,',
'             SUM (CAA.COMMITMENT)       AS TOTAL_COMMITMENT,',
'             SUM (CAA.ACTUAL_AMOUNT)    AS TOTAL_ACTUAL_AMOUNT,',
'             CASE',
'                 WHEN SUM (CAA.COMMITMENT) <> 0',
'                 THEN',
'                        CASE',
'                            WHEN LISTAGG (DISTINCT CAA.PR_NUMBER, ''|'')',
'                                     WITHIN GROUP (ORDER BY CAA.PR_NUMBER)',
'                                     IS NOT NULL',
'                            THEN',
'                                   ''~PR_NUMBER~ ''',
'                                || LISTAGG (DISTINCT CAA.PR_NUMBER, ''|'')',
'                                       WITHIN GROUP (ORDER BY CAA.PR_NUMBER)',
'                        END',
'                     || CASE',
'                            WHEN LISTAGG (DISTINCT CAA.PO_NUMBER, ''|'')',
'                                     WITHIN GROUP (ORDER BY CAA.PO_NUMBER)',
'                                     IS NOT NULL',
'                            THEN',
'                                   ''~PO_NUMBER~ ''',
'                                || LISTAGG (DISTINCT CAA.PO_NUMBER, ''|'')',
'                                       WITHIN GROUP (ORDER BY CAA.PO_NUMBER)',
'                        END',
'             END                        AS TRX_COMMITMENT_NUMBER,',
'             CASE',
'                 WHEN SUM (CAA.ACTUAL_AMOUNT) <> 0',
'                 THEN',
'                        CASE',
'                            WHEN LISTAGG (DISTINCT CAA.RCV_NUMBER, ''|'')',
'                                     WITHIN GROUP (ORDER BY CAA.RCV_NUMBER)',
'                                     IS NOT NULL',
'                            THEN',
'                                   ''~RCV_NUMBER~ ''',
'                                || LISTAGG (DISTINCT CAA.RCV_NUMBER, ''|'')',
'                                       WITHIN GROUP (ORDER BY CAA.RCV_NUMBER)',
'                        END',
'                     || CASE',
'                            WHEN LISTAGG (DISTINCT CAA.INVOICE_NUMBER, ''|'')',
'                                     WITHIN GROUP (ORDER BY CAA.INVOICE_NUMBER)',
'                                     IS NOT NULL',
'                            THEN',
'                                   ''~INVOICE_NUMBER~ ''',
'                                || LISTAGG (DISTINCT CAA.INVOICE_NUMBER, ''|'')',
'                                   WITHIN GROUP (ORDER BY CAA.INVOICE_NUMBER)',
'                        END',
'                     || CASE',
'                            WHEN LISTAGG (DISTINCT CAA.JE_HEADER_ID, ''|'')',
'                                     WITHIN GROUP (ORDER BY CAA.JE_HEADER_ID)',
'                                     IS NOT NULL',
'                            THEN',
'                                   ''~JE_HEADER_ID~ ''',
'                                || LISTAGG (DISTINCT CAA.JE_HEADER_ID, ''|'')',
'                                       WITHIN GROUP (ORDER BY CAA.JE_HEADER_ID)',
'                        END',
'                     || CASE',
'                            WHEN LISTAGG (DISTINCT CAA.INV_TRX_NUMBER, ''|'')',
'                                     WITHIN GROUP (ORDER BY CAA.INV_TRX_NUMBER)',
'                                     IS NOT NULL',
'                            THEN',
'                                   ''~INV_TRX_NUMBER~ ''',
'                                || LISTAGG (DISTINCT CAA.INV_TRX_NUMBER, ''|'')',
'                                   WITHIN GROUP (ORDER BY CAA.INV_TRX_NUMBER)',
'                        END',
'                     || CASE',
'                            WHEN LISTAGG (DISTINCT CAA.AR_TRX_NUMBER, ''|'')',
'                                     WITHIN GROUP (ORDER BY CAA.AR_TRX_NUMBER)',
'                                     IS NOT NULL',
'                            THEN',
'                                   ''~AR_TRX_NUMBER~ ''',
'                                || LISTAGG (DISTINCT CAA.AR_TRX_NUMBER, ''|'')',
'                                   WITHIN GROUP (ORDER BY CAA.AR_TRX_NUMBER)',
'                        END',
'                     || CASE',
'                            WHEN LISTAGG (DISTINCT CAA.FA_ASSET_ID, ''|'')',
'                                     WITHIN GROUP (ORDER BY CAA.FA_ASSET_ID)',
'                                     IS NOT NULL',
'                            THEN',
'                                   ''~FA_ASSET_ID~ ''',
'                                || LISTAGG (DISTINCT CAA.FA_ASSET_ID, ''|'')',
'                                       WITHIN GROUP (ORDER BY CAA.FA_ASSET_ID)',
'                        END',
'                     || CASE',
'                            WHEN LISTAGG (DISTINCT CAA.OPM_ADJ_ID, ''|'')',
'                                     WITHIN GROUP (ORDER BY CAA.OPM_ADJ_ID)',
'                                     IS NOT NULL',
'                            THEN',
'                                   ''~OPM_ADJ_ID~ ''',
'                                || LISTAGG (DISTINCT CAA.OPM_ADJ_ID, ''|'')',
'                                       WITHIN GROUP (ORDER BY CAA.OPM_ADJ_ID)',
'                        END',
'                 ELSE',
'                     NULL',
'             END                        AS TRX_ACTUAL_NUMBER,',
'             NULL                       AS TRANSACTION_ID',
'        FROM APX_BF_COMM_ACT_AMT CAA          --apps.XTD_BF_COMM_ACT_AMT_V CAA',
'             LEFT JOIN APX_BF_ACTIVITY ABA ON CAA.ACTIVITY = ABA.ACTIVITY_CODE',
'    GROUP BY CAA.COMPANY, CAA.PERIOD, CAA.ACTIVITY, ABA.ACTIVITY_NAME',
'    ORDER BY CAA.PERIOD, CAA.ACTIVITY;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "APX_BF_REVENUE_ACTUAL_SUM_V" ("COMPANY", "PERIOD", "PRODUCT", "PRODUCT_DESC", "COA", "COA_DESC", "TOTAL_BALANCE") AS ',
'  SELECT ',
'       rev.COMPANY,',
'        rev.PERIOD,',
'       rev.PRODUCT,',
'       rev.PRODUCT_DESC,',
'       rev.COA,',
'       rev.COA_DESC,',
'    --    rev.ACCOUNT,',
'       SUM(rev.TOTAL_BALANCE) AS TOTAL_BALANCE',
'FROM apps.XTD_BF_REVENUE_ACTUAL_V rev',
'GROUP BY rev.COMPANY,',
'         rev.PERIOD,',
'         rev.PRODUCT,',
'         rev.PRODUCT_DESC,',
'         rev.COA,',
'         rev.COA_DESC',
'        --  rev.ACCOUNT',
'ORDER BY rev.COMPANY,',
'         PERIOD,',
'         PRODUCT,',
'         PRODUCT_DESC,',
'         COA,',
'         COA_DESC',
'        --  ACCOUNT;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "APX_BF_REVENUE_COGS_AMT_V" ("COMPANY", "BULAN", "PRODUCT", "PRODUCT_DESC", "ITEM_CODE", "CATEGORY", "NET_SALES", "HPP") AS ',
'  SELECT company,',
'         bulan,',
'         product,',
'         product_desc,',
'         item_code,',
'         CASE',
'             WHEN coa LIKE ''4%'' THEN ''Revenue''',
'             WHEN coa LIKE ''5%'' THEN ''COGS''',
'             ELSE ''-''',
'         END                AS CATEGORY,',
'         SUM (net_sales)    NET_SALES,',
'         SUM (hpp)          AS HPP',
'    FROM ext.APX_BF_REVENUE_COGS_AMT',
'GROUP BY company,',
'         bulan,',
'         product,',
'         product_desc,',
'         item_code,',
'         CASE',
'             WHEN coa LIKE ''4%'' THEN ''Revenue''',
'             WHEN coa LIKE ''5%'' THEN ''COGS''',
'             ELSE ''-''',
'         END',
'ORDER BY COMPANY,',
'         CASE',
'             WHEN BULAN LIKE ''JAN-%'' THEN 1',
'             WHEN BULAN LIKE ''FEB-%'' THEN 2',
'             WHEN BULAN LIKE ''MAR-%'' THEN 3',
'             WHEN BULAN LIKE ''APR-%'' THEN 4',
'             WHEN BULAN LIKE ''MAY-%'' THEN 5',
'             WHEN BULAN LIKE ''JUN-%'' THEN 6',
'             WHEN BULAN LIKE ''JUL-%'' THEN 7',
'             WHEN BULAN LIKE ''AUG-%'' THEN 8',
'             WHEN BULAN LIKE ''SEP-%'' THEN 9',
'             WHEN BULAN LIKE ''OCT-%'' THEN 10',
'             WHEN BULAN LIKE ''NOV-%'' THEN 11',
'             WHEN BULAN LIKE ''DEC-%'' THEN 12',
'         END,',
'         SUBSTR (BULAN, 5, 2) asc,',
'         ITEM_CODE;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "APX_BF_USER_ACTIVITY_V" ("ACTIVITY_DEPT", "COMPANY_NAME", "ID_ACCESS_DEPT_HR", "TITLE", "DEPT_NAME", "COA", "USERNAME") AS ',
