'           AND arl.APX_ACCESS_RIGHTS_ID = acr.ID',
'           AND arl.DEPT_NAME = beh.dept_name',
'           AND acr.company = beh.COMPANY_NAME',
'    UNION ALL',
'    SELECT DISTINCT',
'           ''Unbudgeted'' || '' | '' || BEH.DEPT_NAME     AS ACTIVITY_DEPT,',
'           beh.company_name,',
'           acr.id                                     AS ID_ACCESS_DEPT_HR,',
'           acr.title,',
'           beh.dept_name,',
'           NULL                                       AS coa, -- no COA for unbudgeted',
'           aua.username',
'      FROM EXT.APX_BF_BUDGET_MONITOR_HDR   BEH,',
'           EXT.apx_bf_access_rights        acr,',
'           EXT.apx_bf_user_access          aua,',
'           EXT.APX_bf_ACCESS_RIGHTS_LISTS  arl',
'     WHERE     aua.apx_access_rights_id = acr.id',
'           AND arl.APX_ACCESS_RIGHTS_ID = acr.ID',
'           AND arl.DEPT_NAME = beh.dept_name',
'           AND acr.company = beh.COMPANY_NAME',
