'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "APX_BUDGET_ENTRY_FULL_V" ("HDR_ID", "BATCH_NO", "VERSION_NO", "COMPANY_NAME", "YEAR", "DEPT_NAME", "BUDGET_TYPE", "AMOUNT_TYPE", "STATUS", "FILE_NAME", "REJECT_NOTES", "HDR_CREATED_DATE", "HDR_CREATED_BY", '
