'      HEADER_ID, BUDGET_CATEGORY, ACTIVITY, COA, COA_DESC, LOCATION,',
'      PRODUCT, UOM, QUANTITY, PRICE, TOTAL, PERIOD,',
'      REJECT_NOTES, CREATED_DATE, CREATED_BY, UPDATED_DATE, UPDATED_BY,',
'      LINE_DESCRIPTION, COST_CENTER, LINE_SOURCE,',
'      ACTIVITY_CODE',
