'                           ''\|''',
'                       ) + 1',
'',
'    UNION ALL',
'    ----------------------------------------------------------------',
'    -- AR_MISC_RECEIPT',
'    SELECT TRIM(REGEXP_SUBSTR(',
'                 REGEXP_SUBSTR(:P5_ACTUAL_TRX, ''~AR_MISC_RECEIPT~([^~]+)'', 1, 1, NULL, 1),',
'                 ''[^|]+'', 1, LEVEL',
'               )) AS val,',
'           ''AR_MISC_RECEIPT'' AS fld',
'    FROM dual',
'    CONNECT BY LEVEL <= REGEXP_COUNT(',
'                           REGEXP_SUBSTR(:P5_ACTUAL_TRX, ''~AR_MISC_RECEIPT~([^~]+)'', 1, 1, NULL, 1),',
'                           ''\|''',
'                       ) + 1',
'',
'    -- UNION ALL',
'    -- ----------------------------------------------------------------',
'    -- -- FA_ASSET_ID',
'    -- SELECT TRIM(REGEXP_SUBSTR(',
'    --              REGEXP_SUBSTR(:P5_ACTUAL_TRX, ''~FA_ASSET_ID~([^~]+)'', 1, 1, NULL, 1),',
'    --              ''[^|]+'', 1, LEVEL',
