'FROM   apx_bf_comm_act_amt base',
'JOIN   params p ',
'       ON p.fld = ''AR_MISC_RECEIPT'' ',
'      AND TRIM(base.AR_MISC_TRX_ID) = TRIM(p.val)',
'WHERE  base.activity = :P5_ACTIVITY_TRX',
