'       NULL LINE_SOURCE,',
'       NULL COMPANY_NAME,',
'       NULL CREATED_DATE,',
'       0 FINAL_AMOUNT,',
'       NULL REJECT_NOTES,',
'       NULL UPDATED_DATE,',
'       NVL(CA.TOTAL_ACTUAL_AMOUNT, 0) AS ACTUAL_AMOUNT,',
'       NVL((0 - NVL(CA.TOTAL_COMMITMENT, 0) - NVL(CA.TOTAL_ACTUAL_AMOUNT, 0)), 0) AS FUND_AVAILABLE,',
