'create or replace PROCEDURE create_apex_user(',
'    p_username IN VARCHAR2,',
'    p_email IN VARCHAR2,',
'    p_password IN VARCHAR2,',
'    p_user_role IN VARCHAR2',
') IS',
'    l_workspace_id NUMBER;',
'    l_app_id NUMBER := 101;',
'BEGIN',
'    l_workspace_id := apex_util.find_security_group_id(p_workspace => ''NCTAPEX'');',
'    apex_util.set_security_group_id(p_security_group_id => l_workspace_id);',
'    ',
'    APEX_UTIL.CREATE_USER(',
'        p_user_name => UPPER(p_username),',
'        p_email_address => p_email,',
'        p_web_password => p_password,',
'        p_change_password_on_first_use => ''Y''',
'    );',
'    ',
'    APEX_ACL.REPLACE_USER_ROLES(',
'        p_application_id => l_app_id,',
'        p_user_name => UPPER(p_username),',
'        p_role_static_ids => apex_t_varchar2(UPPER(p_user_role))',
'    );',
'    ',
'    COMMIT;',
'    ',
'EXCEPTION',
'    WHEN OTHERS THEN',
'        ROLLBACK;',
'        RAISE;',
'END create_apex_user;',
