'create or replace PROCEDURE USER_SYNC_PROC IS',
'    l_workspace_id NUMBER;',
'BEGIN',
'    l_workspace_id := apex_util.find_security_group_id(p_workspace => ''NCTAPEX'');',
'    apex_util.set_security_group_id(p_security_group_id => l_workspace_id);',
'    ',
'    FOR rec IN (',
'        SELECT u.username, u.email, u.user_role',
'        FROM apx_bf_user u',
'        WHERE u.status = ''Active''',
'        AND NOT EXISTS (',
'            SELECT 1 ',
'            FROM apex_workspace_apex_users au ',
'            WHERE au.user_name = u.username',
'        )',
'    ) LOOP',
'        BEGIN',
'            apx_bf_user_mgmt_pkg.create_user(',
'                p_username => rec.username,',
'                p_email_user => rec.email,',
'                p_password_first_time => ''TempPass'' || DBMS_RANDOM.STRING(''X'', 6) || ''!''',
'            );',
'            ',
'            UPDATE apx_bf_user ',
'            SET attribute1 = ''SYNCED_TO_APEX'',',
'                updated_date = SYSTIMESTAMP',
'            WHERE username = rec.username;',
'                ',
'        EXCEPTION',
'            WHEN OTHERS THEN',
'                DBMS_OUTPUT.PUT_LINE(''Sync failed for '' || rec.username || '': '' || SQLERRM);',
'        END;',
'    END LOOP;',
'    ',
'    COMMIT;',
'END USER_SYNC_PROC;',
