'        ELSIF APEX_ACL.HAS_USER_ROLE(p_app_id, p_username, ''DEPARTMENT'') THEN',
'            RETURN ''DEPARTMENT'';',
'        ELSIF APEX_ACL.HAS_USER_ROLE(p_app_id, p_username, ''Contributor'') THEN',
'            RETURN ''Contributor'';',
'        ELSIF APEX_ACL.HAS_USER_ROLE(p_app_id, p_username, ''Reader'') THEN',
'            RETURN ''Reader'';',
'        ELSE',
'            RETURN NULL;',
