'',
'        l_user_id := APEX_UTIL.GET_USER_ID(p_username);',
'',
'        APEX_ACL.REPLACE_USER_ROLES (',
'            p_application_id  => p_app_id,',
'            p_user_name       => upper(p_username),',
'            p_role_static_ids => apex_t_varchar2( upper(p_new_role) ) ',
'        );',
'    ',
'        COMMIT;',
'',
'    EXCEPTION',
'        WHEN OTHERS THEN',
'            ROLLBACK;',
'            RAISE;',
'    END replace_user_role;',
'',
