'                       ) + 1',
'',
'    UNION ALL',
'    ----------------------------------------------------------------',
'    -- INV_TRX_NUMBER',
'    SELECT TRIM(REGEXP_SUBSTR(',
'                 REGEXP_SUBSTR(:P5_ACTUAL_TRX, ''~INV_TRX_NUMBER~([^~]+)'', 1, 1, NULL, 1),',
'                 ''[^|]+'', 1, LEVEL',
'               )) AS val,',
'           ''INV_TRX_NUMBER'' AS fld',
'    FROM dual',
'    CONNECT BY LEVEL <= REGEXP_COUNT(',
'                           REGEXP_SUBSTR(:P5_ACTUAL_TRX, ''~INV_TRX_NUMBER~([^~]+)'', 1, 1, NULL, 1),',
'                           ''\|''',
'                       ) + 1',
'',
'    UNION ALL',
'    ----------------------------------------------------------------',
