'    FROM (',
'        SELECT REGEXP_SUBSTR(:P5_COMMITMENT_TRX, ''PO_NUMBER~\s*([^~]+)'', 1, 1, NULL, 1) AS val ',
'        FROM dual',
'    )',
'    CONNECT BY LEVEL <= REGEXP_COUNT(val, ''\|'') + 1',
'),',
'filtered AS (',
'    SELECT base.pr_number,',
'           base.po_number,',
'           base.po_status,',
'           base.source,',
'           base.commitment',
'      FROM apx_bf_comm_act_amt base',
'     WHERE base.activity   = :P5_ACTIVITY_TRX',
