'   CREATE SEQUENCE  "APX_BF_ACTIVITY_INTERFACE_SEQ"  MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 2 NOCACHE  NOORDER  NOCYCLE  NOKEEP  NOSCALE  GLOBAL ;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "APX_BF_ACTIVITY_MASTER_V" ("ACTIVITY_CODE", "ACTIVITY_NAME", "DEPT_NAME", "COA", "STATUS") AS ',
'  SELECT ACTIVITY_CODE, ACTIVITY_NAME, DEPT_NAME, COA, STATUS',
'FROM EXT.APX_BF_ACTIVITY@ORACONSDEV.NSTI.APPS.NET a',
'WHERE COMPANY_NAME = ''NSTI''',
'  AND STATUS = ''ACTIVE''',
'ORDER BY ',
'  CASE ',
'    WHEN REGEXP_LIKE(ACTIVITY_CODE, ''-([0-9]+)$'') ',
'    THEN TO_NUMBER(REGEXP_SUBSTR(ACTIVITY_CODE, ''-([0-9]+)$'', 1, 1, NULL, 1))',
'    ELSE 0 ',
'  END,',
'  ACTIVITY_CODE, ',
'  ACTIVITY_NAME;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "APX_BF_BUDGET_MONITOR_HDR_V" ("ID", "BATCH_NO", "LATEST_VERSION", "LEDGER_ID", "CHART_OF_ACCOUNTS_ID", "SHORT_NAME", "COMPANY_NAME", "YEAR", "DEPT_NAME", "BUDGET_TYPE", "AMOUNT_TYPE", "STATUS", "REJECT_NOTE'
||'S", "CREATED_DATE", "CREATED_BY", "UPDATED_DATE", "UPDATED_BY") AS ',
'  SELECT ',
'    "ID",',
'    "BATCH_NO",',
'    "VERSION_NO",',
'    "LEDGER_ID",',
'    "CHART_OF_ACCOUNTS_ID",',
'    "SHORT_NAME",',
'    "COMPANY_NAME",',
'    "YEAR",',
'    "DEPT_NAME",',
'    "BUDGET_TYPE",',
'    "AMOUNT_TYPE",',
'    "STATUS",',
'    "REJECT_NOTES",',
'    "CREATED_DATE",',
'    "CREATED_BY",',
'    "UPDATED_DATE",',
'    "UPDATED_BY"',
'FROM ',
'    "APX_BF_BUDGET_ENTRY_HDR"',
'WHERE STATUS = ''APPROVED'';',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "APX_BF_BUDGET_MONITOR_LNS_V" ("ID", "HEADER_ID", "VERSION_NO", "BUDGET_CATEGORY", "ACTIVITY", "COA", "COA_DESC", "LOCATION", "QUANTITY", "PRICE", "ORIGINAL_AMOUNT", "COMMITMENT", "FINAL_AMOUNT", "ACTUAL_USA'
||'GE", "FUND_AVAILABLE", "PERIOD", "REJECT_NOTES", "CREATED_DATE", "CREATED_BY", "UPDATED_DATE", "UPDATED_BY", "LINE_DESCRIPTION", "COST_CENTER", "LINE_SOURCE", "ACTIVITY_CODE", "BATCH_NO") AS ',
'  WITH header_versions AS (',
'    SELECT ',
'        h.ID,',
'        h.VERSION_NO,',
'        h.BATCH_NO,',
'        h.STATUS,',
'        RANK() OVER (PARTITION BY h.ID ORDER BY h.VERSION_NO) AS version_rank,',
'        FIRST_VALUE(h.VERSION_NO) OVER (',
'            PARTITION BY h.ID ',
'            ORDER BY CASE WHEN h.STATUS = ''APPROVED'' THEN 0 ELSE 1 END, ',
'                     h.VERSION_NO DESC',
'            ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING',
'        ) AS latest_approved_version',
'    FROM ',
'        "APX_BF_BUDGET_ENTRY_HDR" h',
')',
'SELECT ',
'    a.ID,',
'    a.HEADER_ID,',
'    ah.VERSION_NO,',
'    a.BUDGET_CATEGORY,',
'    a.ACTIVITY,',
'    a.COA,',
'    a.COA_DESC,',
'    a.LOCATION,',
'    a.QUANTITY,',
'    a.PRICE,',
'    orig_a.TOTAL AS ORIGINAL_AMOUNT,',
'    NULL AS COMMITMENT,',
'    a.TOTAL AS FINAL_AMOUNT,',
'    NULL AS ACTUAL_USAGE,',
'    NULL AS FUND_AVAILABLE,',
'    a.PERIOD,',
'    a.REJECT_NOTES,',
'    a.CREATED_DATE,',
'    a.CREATED_BY,',
'    a.UPDATED_DATE,',
'    a.UPDATED_BY,',
'    a.LINE_DESCRIPTION,',
'    a.COST_CENTER,',
'    a.LINE_SOURCE,',
'    a.ACTIVITY_CODE,',
'    ah.BATCH_NO',
'FROM ',
'    "APX_BF_BUDGET_ENTRY_LNS" a',
'JOIN ',
'    header_versions ah ',
'    ON a.HEADER_ID = ah.ID ',
'    AND ah.VERSION_NO = ah.latest_approved_version',
'JOIN ',
'    header_versions oh ',
'    ON a.HEADER_ID = oh.ID ',
'    AND oh.version_rank = 1',
'JOIN ',
'    "APX_BF_BUDGET_ENTRY_LNS" orig_a ',
'    ON orig_a.ID = a.ID ',
'    AND orig_a.HEADER_ID = oh.ID;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "APX_BF_COMM_ACT_SUM_V" ("ACTIVITY", "ACTIVITY_MASTER", "ACTIVITY_CODE", "PERIOD", "TOTAL_COMMITMENT", "TOTAL_ACTUAL_AMOUNT", "VARIANCE", "ACTUAL_PERCENTAGE", "TRX_COMMITMENT_NUMBER", "TRX_ACTUAL_NUMBER") AS'
||' ',
'  WITH AGGREGATED_DATA AS (',
'    SELECT ',
'        a.PERIOD,',
'        a.ACTIVITY,',
'        CASE ',
'            WHEN INSTR(a.ACTIVITY, ''|'') > 0 THEN ',
