'                       ) + 1',
'',
'    -- UNION ALL',
'    -- ----------------------------------------------------------------',
'    -- -- FA_ASSET_ID',
'    -- SELECT TRIM(REGEXP_SUBSTR(',
'    --              REGEXP_SUBSTR(:P5_ACTUAL_TRX, ''~FA_ASSET_ID~([^~]+)'', 1, 1, NULL, 1),',
'    --              ''[^|]+'', 1, LEVEL',
'    --            )) AS val,',
'    --        ''FA_ASSET_ID'' AS fld',
'    -- FROM dual',
'    -- CONNECT BY LEVEL <= REGEXP_COUNT(',
'    --                        REGEXP_SUBSTR(:P5_ACTUAL_TRX, ''~FA_ASSET_ID~([^~]+)'', 1, 1, NULL, 1),',
'    --                        ''\|''',
'    --                    ) + 1',
'',
'    UNION ALL',
'    ----------------------------------------------------------------',
'    -- OPM_ADJ_ID',
'    SELECT TRIM(REGEXP_SUBSTR(',
'                 REGEXP_SUBSTR(:P5_ACTUAL_TRX, ''~OPM_ADJ_ID~([^~]+)'', 1, 1, NULL, 1),',
'                 ''[^|]+'', 1, LEVEL',
'               )) AS val,',
'           ''OPM_ADJ_ID'' AS fld',
