'        WHEN STATUS=''SUBMITTED'' THEN ''#7CFC00''',
'        WHEN STATUS=''IN PROCESS'' THEN ''#FFFF00''',
'        WHEN STATUS = ''APPROVED'' THEN ''#3FEB50''',
