'           WHEN SUBSTR(CA.PERIOD, 1, 3) = ''DEC'' THEN 12',
'           ELSE 0',
'       END LINE_NUMBER,',
'       NULL LINE_SOURCE,',
'       NULL COMPANY_NAME,',
'       NULL CREATED_DATE,',
'       0 FINAL_AMOUNT,',
'       NULL REJECT_NOTES,',
'       NULL UPDATED_DATE,',
'       NVL(CA.TOTAL_ACTUAL_AMOUNT, 0) AS ACTUAL_AMOUNT,',
