'            p_display_location => APEX_ERROR.C_INLINE_IN_NOTIFICATION',
'        );',
'        RETURN;',
'    END IF;',
'    ',
'    l_role_static_id := CASE UPPER(l_user_role)',
'        WHEN ''DEPARTMENT'' THEN ''DEPARTMENT''',
'        WHEN ''ADMINISTRATOR'' THEN ''ADMINISTRATOR'' ',
