'        EXCEPTION',
'            WHEN OTHERS THEN',
'                DBMS_OUTPUT.PUT_LINE(''Sync failed for '' || rec.username || '': '' || SQLERRM);',
'        END;',
'    END LOOP;',
'    ',
'    COMMIT;',
