'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "APX_BF_COMM_ACT_SUM_1_V" ("ACTIVITY", "ACTIVITY_MASTER", "ACTIVITY_CODE", "PERIOD", "TOTAL_COMMITMENT", "TOTAL_ACTUAL_AMOUNT", "VARIANCE", "ACTUAL_PERCENTAGE", "TRX_COMMITMENT_NUMBER", "TRX_ACTUAL_NUMBER") '
||'AS ',
'  WITH',
'        AGGREGATED_DATA',
'        AS',
'            (  SELECT a.PERIOD,',
'                      a.ACTIVITY,',
'                      CASE',
'                          WHEN INSTR (a.ACTIVITY, ''|'') > 0',
'                          THEN',
'                              TRIM (',
'                                  SUBSTR (a.ACTIVITY,',
'                                          1,',
'                                          INSTR (a.ACTIVITY, ''|'') - 1))',
'                          ELSE',
'                              a.ACTIVITY',
'                      END',
'                          AS ACTIVITY_BASE_NAME,',
'                      SUM (a.COMMITMENT)',
'                          AS TOTAL_COMMITMENT,',
'                      SUM (a.ACTUAL_AMOUNT)',
'                          AS TOTAL_ACTUAL_AMOUNT,',
'                      SUM (a.COMMITMENT - a.ACTUAL_AMOUNT)',
'                          AS VARIANCE,',
'                      max(DISTINCT a.PR_NUMBER)',
'--                          WITHIN GROUP (ORDER BY a.PR_NUMBER)',
'                          AS PR_NUMBERS,',
'                      max (DISTINCT a.PO_NUMBER)',
'--                          WITHIN GROUP (ORDER BY a.PO_NUMBER)',
'                          AS PO_NUMBERS,',
'                      max (DISTINCT a.RCV_NUMBER)',
'--                          WITHIN GROUP (ORDER BY a.RCV_NUMBER)',
'                          AS RCV_NUMBERS,',
'                      max (DISTINCT a.INVOICE_NUMBER)',
'--                          WITHIN GROUP (ORDER BY a.INVOICE_NUMBER)',
'                          AS INVOICE_NUMBERS,',
'                      max (DISTINCT a.JE_HEADER_ID)',
'--                          WITHIN GROUP (ORDER BY a.JE_HEADER_ID)',
'                          AS JE_HEADER_IDS,',
'                      max (DISTINCT a.INV_TRX_NUMBER)',
'--                          WITHIN GROUP (ORDER BY a.INV_TRX_NUMBER)',
'                          AS INV_TRX_NUMBERS,',
'                      max (DISTINCT a.AR_TRX_NUMBER)',
'--                          WITHIN GROUP (ORDER BY a.AR_TRX_NUMBER)',
'                          AS AR_TRX_NUMBERS,',
'                      max (DISTINCT a.AR_MISC_TRX_ID)',
'--                          WITHIN GROUP (ORDER BY a.AR_MISC_TRX_ID)',
'                          AS AR_MISC_TRX_IDS,',
'                      -- LISTAGG(DISTINCT a.FA_ASSET_ID, ''|'') WITHIN GROUP (ORDER BY a.FA_ASSET_ID) AS FA_ASSET_IDS,',
'                      max (DISTINCT a.OPM_ADJ_ID)',
'--                          WITHIN GROUP (ORDER BY a.OPM_ADJ_ID)',
'                          AS OPM_ADJ_IDS',
'                 FROM APX_BF_COMM_ACT_AMT a',
'             GROUP BY a.PERIOD,',
'                      a.ACTIVITY,',
'                      CASE',
'                          WHEN INSTR (a.ACTIVITY, ''|'') > 0',
'                          THEN',
'                              TRIM (',
'                                  SUBSTR (a.ACTIVITY,',
'                                          1,',
'                                          INSTR (a.ACTIVITY, ''|'') - 1))',
'                          ELSE',
'                              a.ACTIVITY',
'                      END)',
'      SELECT AD.ACTIVITY,',
'             AM.ACTIVITY_NAME          AS ACTIVITY_MASTER,',
'             MAX (AM.ACTIVITY_CODE)    AS ACTIVITY_CODE,',
'             AD.PERIOD,',
'             AD.TOTAL_COMMITMENT,',
'             AD.TOTAL_ACTUAL_AMOUNT,',
'             AD.VARIANCE,',
'             CASE',
'                 WHEN AD.TOTAL_COMMITMENT = 0',
'                 THEN',
'                     0',
'                 ELSE',
'                     ROUND (',
'                         (AD.TOTAL_ACTUAL_AMOUNT / AD.TOTAL_COMMITMENT) * 100,',
'                         2)',
'             END                       AS ACTUAL_PERCENTAGE,',
'             CASE',
'                 WHEN AD.TOTAL_COMMITMENT IS NOT NULL',
'                 THEN',
'                        CASE',
'                            WHEN AD.PR_NUMBERS IS NOT NULL',
'                            THEN',
'                                ''~PR_NUMBER~ '' || AD.PR_NUMBERS',
'                            ELSE',
'                                ''''',
'                        END',
'                     || CASE',
'                            WHEN AD.PO_NUMBERS IS NOT NULL',
'                            THEN',
'                                ''~PO_NUMBER~ '' || AD.PO_NUMBERS',
'                            ELSE',
'                                ''''',
'                        END',
'                 ELSE',
'                     NULL',
'             END                       AS TRX_COMMITMENT_NUMBER,',
'             CASE',
'                 WHEN AD.TOTAL_ACTUAL_AMOUNT IS NOT NULL',
'                 THEN',
'                        CASE',
