'    PROCEDURE lock_user_job(p_username in varchar2) IS',
'    BEGIN',
'        dbms_scheduler.set_job_argument_value (',
'            job_name => ''APX_BF_LOCK_USER_JOB'',',
'            argument_position => 1,',
'            argument_value => p_username ',
'        );',
'        dbms_scheduler.run_job (',
'            job_name => ''APX_BF_LOCK_USER_JOB'',',
'            use_current_session => false ',
'        );',
'',
'        COMMIT;',
'',
'    EXCEPTION',
'        WHEN OTHERS THEN',
'            ROLLBACK;',
'            RAISE;',
'    END lock_user_job;',
'',
'    PROCEDURE deactivate_user_job (p_username in varchar2) IS',
'        l_workspace_id      number;',
'    BEGIN',
'',
'    l_workspace_id := apex_util.find_security_group_id (p_workspace => g_workspace);',
'    apex_util.set_security_group_id (p_security_group_id => l_workspace_id); ',
'',
'        APEX_UTIL.LOCK_ACCOUNT(p_user_name => upper(p_username));',
'',
'        UPDATE APX_BF_USER ',
'        SET',
'            INACTIVE_DATE = SYSDATE,',
'            STATUS = ''Inactive''',
'        WHERE USERNAME = p_username;           ',
'',
'    COMMIT;',
'        ',
'    EXCEPTION',
'        WHEN OTHERS THEN',
'            ROLLBACK;',
'            RAISE;',
'',
'    END deactivate_user_job;',
